import React , { useState } from 'react'

export default function Form() {

    const [currentSavings, setCurrentSavings] = useState(''); // for Current Savings  
    const [yearlySavings, setYearlySavings] = useState(''); // for Yearly Savings  
    // const [] = useState(''); // for savings  
    // const [] = useState(''); // for savings  

    // const submitHandler = ()=>{
    //     console.log("Submitted")
    // }

    setCurrentSavings = ()=>{
        useState(currentSavings); 
    }

    setYearlySavings = ()=>{
        useState(yearlySavings); 
    }


    return (
        <form className="form">
            <div className="input-group">
                <p>
                    <label htmlFor="current-savings">{currentSavings}($)</label>
                    <input type="number" id="current-savings" />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution" onInput={setCurrentSavings}/>
                </p>
            </div>
            <div className="input-group">
                <p>
                    <label htmlFor="expected-return">
                        Expected Interest (%, per year)
                    </label>
                    <input type="number" id="expected-return" />
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration" />
                </p>
            </div>
            <p className="actions">
                <button type="reset" className="buttonAlt">
                    Reset
                </button>
                <button type="submit" className="button" onSubmit={submitHandler}>
                    Calculate
                </button>
            </p>
        </form>
    )
}
